// Translation type - flexible to allow different string values per language
export type Translations = Record<string, string>;

export function getTranslations(locale: Locale): Translations {
  return (translations[locale] as Translations) || (translations.fr as Translations);
}

export function formatPrice(locale: Locale, variant: 'standard' | 'platinum' | 'pair' = 'standard'): string {
  const config = LOCALES[locale];
  const price = variant === 'platinum' ? config.pricePlatinum : variant === 'pair' ? config.pricePair : config.price;
  
  if (config.currency === 'KRW' || config.currency === 'JPY') {
    return `${config.currencySymbol}${price.toLocaleString()}`;
  }
  if (config.currency === 'USD') return `$${price}`;
  if (config.currency === 'EUR') return `${price}‚Ç¨`;
  return `${config.currencySymbol}${price}`;
}

export function formatAccessoryPrice(locale: Locale, tier: 'standard' | 'premium' | 'superpack' = 'standard'): string {
  const config = LOCALES[locale];
  const price = tier === 'superpack' ? config.priceSuperPack : tier === 'premium' ? config.pricePremium : config.priceAccessory;
  if (config.currency === 'KRW' || config.currency === 'JPY') {
    return `${config.currencySymbol}${price.toLocaleString()}`;
  }
  if (config.currency === 'USD') return `$${price}`;
  if (config.currency === 'EUR') return `${price}‚Ç¨`;
  return `${config.currencySymbol}${price}`;
}

export const LOCALE_LIST = Object.values(LOCALES);
export const DEFAULT_LOCALE: Locale = 'fr';
